mapper.select:
  	 SELECT * FROM
  	 (
	  	 SELECT ROWNUM as rn, t.* FROM
	  	 (
	  	 	SELECT * FROM freereply
	  		WHERE bno = #{bno}
	  		ORDER BY rno DESC
	  	 ) t
  	 )

  	 WHERE rn > (#{paging.pageNum} - 1) * #{paging.countPerPage}
  	 AND rn <= #{paging.pageNum} * #{paging.countPerPage}

mapper.update:
UPDATE freereply SET 
	reply = #{reply}
	update_date = SYSDATE
WHERE rno = #{rno}
